import React, { useReducer } from 'react';
import uuid from 'uuid';
import ContactContext from './contactContext';
import contactReducer from './contactReducer';
import {
  ADD_CONTACT,
  DELETE_CONTACT,
  SET_CURRENT,
  CLEAR_CURRENT,
  UPDATE_CONTACT,
  FILTER_CONTACTS,
  CLEAR_FILTER
} from '../types';

const ContactState = props => {
  const initialState = {
    contacts: [
      {
        id: 1,
        name: 'Age Miil',
        email: 'jill@john.ee',
        phone: '334455',
        type: 'personal'
      },
      {
        id: 2,
        name: 'Ralf Jalakas',
        email: 'thomas@john.ee',
        phone: '22334455',
        type: 'personal'
      },
      {
        id: 3,
        name: 'Siret Ruul',
        email: 'siret.ruul@hotmail.com',
        phone: '334455190',
        type: 'professional'
      }
    ]
  };

  const [state, dispatch] = useReducer(contactReducer, initialState);

  //Add contact
  const addContact = contact => {
    contact.id = uuid.v4();
    dispatch({ type: ADD_CONTACT, payload: contact });
  };

  //Delete contact. võtab sisse aind id
  const deleteContact = id => {
    //kustutamiseks laheb reaalselt reduceris vastavalt siis typele ja payloadile
    dispatch({ type: DELETE_CONTACT, payload: id });
  };

  //Set current conatct

  //clear current contact

  //update contact

  //filter contacts

  //clear filter

  return (
    <ContactContext.Provider
      value={{
        contacts: state.contacts,
        addContact,
        deleteContact
      }}
    >
      {props.children}
    </ContactContext.Provider>
  );
};

export default ContactState;
